SELECT CTA.CODIGO,
	CTA.CNPJCPF,
	CTA.NOME,
	CTA.INSCRCRC,
	CTA.DDD,
	CTA.FONE,
	CTA.FAX,
	CTA.ENDLOGRADOURO,
	CTA.ENDNUMERO,
	CTA.ENDCOMPLEMENTO,
	CTA.BAIRRO,
	CTA.CEP,
	CTA.MUN_UFD_SIGLA,
	CTA.MUN_CODIGO,
	(SELECT max(MUN.NOME) FROM MUN WHERE MUN.CODIGO = CTA.MUN_CODIGO) AS CIDADE,
	CTA.EMAIL
FROM CTA
ORDER BY CTA.CODIGO