SELECT DEP.EMP_CODIGO,
	DEP.EPG_CODIGO,
	DEP.SEQ,
	DEP.NOME,
	DEP.SALARIOFAMILIA,
	DEP.AUXILIOCRECHE,
	DEP.SEXO,
	DEP.MUN_UFD_SIGLA_NASC,
	DEP.MUN_CODIGO_NASC,
	(SELECT max(MUN.NOME) FROM MUN WHERE MUN.CODIGO = DEP.MUN_CODIGO_NASC AND MUN.UFD_SIGLA = DEP.MUN_UFD_SIGLA_NASC) AS END_CIDADE,
	DEP.NASCDATA,
	DEP.CERTIDAONUMERO,
	DEP.CERTIDAOLIVRO,
	DEP.CERTIDAOFOLHA,
	DEP.CERTIDAOCARTORIO,
	DEP.DEPENDDTINICIAL,
	DEP.DEPENDDTFINAL,
	DEP.SALARIOFAMILIADTINICIAL,
	DEP.SALARIOFAMILIADTFINAL,
	DEP.AUXILIOCRECHEDTINICIAL,
	DEP.AUXILIOCRECHEDTFINAL,
	DEP.TB_TIP_DEP_CODIGO,
	DEP.PLANOSAUDE,
	DEP.CODIGOPLANOSAUDE,
	DEP.CERTIDAOMATRICULA,
	DEP.CPF
FROM DEP
ORDER BY DEP.EMP_CODIGO, DEP.EPG_CODIGO, DEP.SEQ