SELECT DISTINCT
       SJOR_INT.EMP_CODIGO,
       SJOR_INT.SJOR_ID,
       SJOR_INT.ORDEM,
       SJOR_INT.TIPOINTERVALO,
       SJOR_INT.HORAENTRADA,
       SJOR_INT.HORASAIDA 
  FROM SJOR_INT
 INNER JOIN SJOR
    ON SJOR_INT.EMP_CODIGO = SJOR.EMP_CODIGO 
   AND SJOR_INT.SJOR_ID = SJOR.ID
 ORDER BY SJOR_INT.EMP_CODIGO, SJOR_INT.SJOR_ID;