SELECT CODIGO,
       CNPJ,
       RAZAOSOCIAL,
       LOGRADOURO,
       NUMERO,
       BAIRRO,
       CEP,
       MUN_CODIGO,
       UFD_SIGLA,
       ENDCOMPLEMENTO,
       (SELECT MAX(MUN.NOME) FROM MUN WHERE MUN.CODIGO = EDU.MUN_CODIGO AND MUN.UFD_SIGLA = EDU.UFD_SIGLA) AS NOME_MUNICIPIO
 FROM EDU
ORDER BY 1,2
