SELECT FO_ESD.CODEMPRESA,
       FO_ESD.ESL_ID,
       FO_ESD.EVENTO_RECIBO,
       FO_ESE.EVENTO,
       FO_FUC.CODFUNCAO AS CODIGO_CARGO,
       FO_FUN.CODEMPREGADO AS CODIGO_EMPREGADO,
       FO_FUN.MATRICULA AS MATRICULA_EMPREGADO,
       FO_SOC.CODSOCIO  AS CODIGO_SOCIO,
       FO_SOC.MATRICULA AS MATRICULA_SOCIO
FROM FO_ESD
INNER JOIN FO_ESE
ON (FO_ESD.ESE_ID = FO_ESE.ESE_ID)
LEFT JOIN FO_FUC
ON (FO_ESD.ESL_ID = FO_FUC.ESL_ID)
LEFT JOIN FO_FUN
ON (FO_ESD.ESL_ID = FO_FUN.ESL_ID)
LEFT JOIN FO_SOC
ON (FO_ESD.ESL_ID = FO_SOC.ESL_ID)
WHERE EVENTO_RECIBO <> '' AND EVENTO IN ('S-1000', 'S-1030', 'S-2200', 'S-2300')
ORDER BY 1, 4
