SELECT CODIG_EMPR,
	RAZAO_EMPR,
	TPEND_EMPR,
	ENDER_EMPR,
	NUMER_EMPR,
	COMPL_EMPR,
	BAIRR_EMPR,
	(SELECT DESCR_MUNI FROM TABGRMUNI WHERE TABGRMUNI.CODIG_MUNI = TABGREMPR.MUNIC_EMPR) AS CIDADE,
	(SELECT ESTAD_MUNI FROM TABGRMUNI WHERE TABGRMUNI.CODIG_MUNI = TABGREMPR.MUNIC_EMPR) AS ESTADO,
	NOCEP_EMPR,
	NODDD_EMPR,
	NOTEL_EMPR,
	NOCGC_EMPR,
	NOCEI_EMPR,
	NOIES_EMPR,
	NOIMU_EMPR,
	NMTIT_EMPR,
	NOCPF_EMPR,
	NONAT_EMPR,
	CDATI_EMPR,
	NMATI_EMPR,
	REPLACE(EMAIL_EMPR, '\n', '') AS EMAIL_EMPR,
	SIMPL_EMPR,
	CATEG_EMPR,
	INIAT_EMPR,
	FIMAT_EMPR,
	LOCAL_EMPR,
	NOMCC_EMPR,
	SINDI_EMPR,
	CDFPA_EMPR,
	CDTER_EMPR,
	NOPRO_EMPR,
	NOBFG_EMPR,
	NOAFG_EMPR,
	NOCFG_EMPR,
	HORAR_EMPR,
	CDGPS_EMPR,
	CDCAG_EMPR,
	ALIME_EMPR,
	DTBAS_EMPR,
	PESAT_EMPR,
	PEFPA_EMPR,
	PEFIL_EMPR,
	PETER_EMPR,
	CAST(PE13O_EMPR AS NUMERIC(4,2)) AS PE13O_EMPR,
	CAST(PEFER_EMPR AS NUMERIC(4,2)) AS PEFER_EMPR,
	PEPRO_EMPR,
	PEAUT_EMPR,
	ARRED_EMPR,
	CAPIT_EMPR,
	LANCB_EMPR,
	APUCB_EMPR,
	FECCB_EMPR,
	JTACO_EMPR,
	REGCO_EMPR,
	DTREG_EMPR,
	RESPO_EMPR,
	(SELECT NOCPF_RESP FROM TABGRRESP WHERE TABGRRESP.CODIG_RESP = TABGREMPR.RESPO_EMPR) AS RESP_CPF,
	(SELECT NOMER_RESP FROM TABGRRESP WHERE TABGRRESP.CODIG_RESP = TABGREMPR.RESPO_EMPR) AS RESP_NOME,
	(SELECT ENDER_RESP FROM TABGRRESP WHERE TABGRRESP.CODIG_RESP = TABGREMPR.RESPO_EMPR) AS RESP_END,
	(SELECT NUMER_RESP FROM TABGRRESP WHERE TABGRRESP.CODIG_RESP = TABGREMPR.RESPO_EMPR) AS RESP_NUME,
	(SELECT COMPL_RESP FROM TABGRRESP WHERE TABGRRESP.CODIG_RESP = TABGREMPR.RESPO_EMPR) AS RESP_COMPLE,
	(SELECT BAIRR_RESP FROM TABGRRESP WHERE TABGRRESP.CODIG_RESP = TABGREMPR.RESPO_EMPR) AS RESP_BAIRRO,
	
	(SELECT DESCR_MUNI FROM TABGRMUNI WHERE TABGRMUNI.CODIG_MUNI = (SELECT MUNIC_RESP
																	FROM TABGRRESP
																	WHERE TABGRRESP.CODIG_RESP = TABGREMPR.RESPO_EMPR)) AS RESP_CIDADE,
	(SELECT ESTAD_MUNI FROM TABGRMUNI WHERE TABGRMUNI.CODIG_MUNI = (SELECT MUNIC_RESP
																	FROM TABGRRESP
																	WHERE TABGRRESP.CODIG_RESP = TABGREMPR.RESPO_EMPR)) AS RESP_ESTADO,																
	
	(SELECT NOCEP_RESP FROM TABGRRESP WHERE TABGRRESP.CODIG_RESP = TABGREMPR.RESPO_EMPR) AS RESP_CEP,
	(SELECT NODDD_RESP FROM TABGRRESP WHERE TABGRRESP.CODIG_RESP = TABGREMPR.RESPO_EMPR) AS RESP_DDD,
	(SELECT NOTEL_RESP FROM TABGRRESP WHERE TABGRRESP.CODIG_RESP = TABGREMPR.RESPO_EMPR) AS RESP_FONE,
	(SELECT EMAIL_RESP FROM TABGRRESP WHERE TABGRRESP.CODIG_RESP = TABGREMPR.RESPO_EMPR) AS RESP_EMAIL,
	(SELECT NORGS_RESP FROM TABGRRESP WHERE TABGRRESP.CODIG_RESP = TABGREMPR.RESPO_EMPR) AS RESP_RG,
	PLANO_EMPR,
	LCCTB_EMPR,
	NIVCC_EMPR,
	CDLOC_EMPR,
	NORUR_EMPR,
	OBSER_EMPR,
	ESTAB_EMPR,
	MATRI_EMPR
FROM TABGREMPR
ORDER BY 1