SELECT DISTINCT TABFPHORC.CODIG_HORC,
	TABFPHORC.DESCR_HORC,
	ENTRA_HORI AS ENTRADA,
	RSAID_HORI AS SAIDA_INTERVALO,
	RVOLT_HORI AS ENTRADA_INTERVALO,
	SAIDA_HORI AS SAIDA
FROM TABFPHORC  LEFT JOIN TABFPHORI ON (TABFPHORI.CODIG_HORI = TABFPHORC.CODIG_HORC )
ORDER BY TABFPHORC.CODIG_HORC ASC, TABFPHORI.DATAH_HORI ASC
