SELECT EMPRE_LOCA,
CODIG_LOCA,
DESCR_LOCA,
NOCGC_LOCA,
NOCEI_LOCA,
CDFPA_LOTA,
CDTER_LOTA,
ENDER_LOCA,
NUMER_LOCA,
COMPL_LOCA,
BAIRR_LOCA,
NOCEP_LOCA,
CDGPS_LOCA,
(SELECT FIRST 1 PEFPA_EMPR 
 FROM TABGREMPR
 WHERE TABGREMPR.CODIG_EMPR = TABFPLOCA.EMPRE_LOCA)
FROM TABFPLOCA LEFT JOIN TABFPLOTA on TABFPLOCA.EMPRE_LOCA = TABFPLOTA.EMPRE_LOTA
					AND TABFPLOCA.CODIG_LOCA = TABFPLOTA.CODIG_LOTA 
ORDER BY 1,2