SELECT CODIG_SIND, 
       DESCR_SIND,
	   TPEND_SIND,
	   ENDER_SIND,
	   NUMER_SIND,
	   COMPL_SIND,
	   BAIRR_SIND,
	   (SELECT DESCR_MUNI FROM TABGRMUNI WHERE TABGRMUNI.CODIG_MUNI = TABFPSIND.MUNIC_SIND) AS CIDADE,
	   (SELECT ESTAD_MUNI FROM TABGRMUNI WHERE TABGRMUNI.CODIG_MUNI = TABFPSIND.MUNIC_SIND) AS ESTADO,
	   NOCEP_SIND, NODDD_SIND, NOTEL_SIND, NOCGC_SIND, ENTID_SIND, DTBAS_SIND
FROM TABFPSIND
ORDER BY 1