SELECT EMPREGADOS.COD_EMPRESA,
	   EMPREGADOS.COD_EMPREGADO,
	   SST_ASO.RESULTADO,	   
	   max(SST_ASO."DATA") AS DATA
FROM SST_ASO INNER JOIN EMPREGADOS
					ON SST_ASO.IDEMPRESA  = EMPREGADOS.IDEMPRESA 
				   AND SST_ASO.IDEMPREGADO = EMPREGADOS.IDEMPREGADO  
WHERE SST_ASO.TIPOASO = 0		     

GROUP BY EMPREGADOS.COD_EMPRESA,
	   EMPREGADOS.COD_EMPREGADO,
	   SST_ASO.RESULTADO
ORDER BY 1,2