SELECT SC00_EMPRESA.EMPRESA AS COD_EMPRESA,
       SC00_FILIAL.FILIAL AS COD_FILIAL,
       SC00_DEPTO.DEPTO AS COD_DEPTO,
       SC00_DEPTO.NOME AS NOME,
       CA06_CODLOGRAD.ABREVIACAO AS COD_LOGR,
       SC00_DEPTO.ENDERECO AS ENDERECO,
       SC00_DEPTO.NUMERO AS NRO_RUA,
       SC00_DEPTO.BAIRRO AS BAIRRO,
       SC00_DEPTO.CEP AS CEP,
       CA06_MUNICIPIO.CODIGOFEDERAL AS COD_MUN,
       CA06_MUNICIPIO.UF AS UF,
       SC00_DEPTO.INSCRICAO AS CGC,
       CFG.LCOD_FPAS AS COD_FPAS,
       CASE SC00_DEPTO.TPINSCRICAO
         WHEN 1 THEN 'CNPJ'
         WHEN 2 THEN 'CPF'
         WHEN 3 THEN 'CEI'
       END AS TP_INSCR,
       CFG.LCOD_GFIP AS COD_GFIP,
       CFG.LCOD_GPS AS COD_GPS,
       SC00_DEPTO.NOMEFANTASIA AS NOMEFANTASIA,
       SC00_DEPTO.IDDEPTO AS IDDEPARTAMENTO,
       CFG.LCODIGOESOCIAL AS CODIGOESOCIAL,
       CAST(CFG.LDATAATIVIDADE AS TIMESTAMP) AS DATAATIVIDADE,
       CFG.LIDTPLOTACAO AS IDTPLOTACAO,
       CA06_CODLOGRAD.IDCODLOGRAD AS IDCODLOGRAD,
       CFG.LIDFPASCODIGO AS IDFPASCODIGO,
       SC00_DEPTO.CODIGOESOCIAL
  FROM SC00_EMPRESA
  JOIN SC00_FILIAL
    ON (SC00_EMPRESA.IDEMPRESA = SC00_FILIAL.IDEMPRESA)
  JOIN SC00_DEPTO
    ON (SC00_FILIAL.IDFILIAL = SC00_DEPTO.IDFILIAL)
  LEFT JOIN CA06_CODLOGRAD
    ON (CA06_CODLOGRAD.IDCODLOGRAD = SC00_DEPTO.IDCODLOGRAD)
  LEFT JOIN CA06_MUNICIPIO
    ON (SC00_DEPTO.IDMUNICIPIO = CA06_MUNICIPIO.IDMUNICIPIO)
  LEFT JOIN SP_BUSCARCONFIGDEPARTAMENTO(SC00_DEPTO.IDDEPTO) CFG
    ON (CFG.LIDDEPARTAMENTO = SC00_DEPTO.IDDEPTO)
 ORDER BY 1, 3;