       TRANSFERENCIAS."DATA" AS DATA_TRANSFERENCIA,
       TRANSFERENCIAS.COD_EMPRESAORIGEM,
       TRANSFERENCIAS.COD_EMPREGADOORIGEM
FROM EMPREGADOS
LEFT JOIN FP_ESOCIALTABELA FP ON FP.IDESOCIALTABELA = EMPREGADOS.IDCATEGORIAESOCIAL
LEFT JOIN MOV_TRANSF ON EMPREGADOS.COD_EMPRESA = MOV_TRANSF.COD_EMPRESA 
                     AND EMPREGADOS.COD_EMPREGADO = MOV_TRANSF.COD_EMPREGADO
                     AND MOV_TRANSF.MOTIVOINICIAL IN ('ADMISSAO', 'TRANSFERENCIA DEFINITIVA')
LEFT JOIN (
    SELECT TRANSF_EMPREG.COD_EMPRESAORIGEM, 
           TRANSF_EMPREGDET.COD_EMPREGADOORIGEM, 
           TRANSF_EMPREG."DATA"
    FROM TRANSF_EMPREG
    INNER JOIN TRANSF_EMPREGDET ON TRANSF_EMPREG.IDTRANSF_EMPREG = TRANSF_EMPREGDET.IDTRANSF_EMPREG
) AS TRANSFERENCIAS ON EMPREGADOS.COD_EMPRESA = TRANSFERENCIAS.COD_EMPRESAORIGEM
                    AND EMPREGADOS.COD_EMPREGADO = TRANSFERENCIAS.COD_EMPREGADOORIGEM
ORDER BY 1, 2;
