SELECT EMPRESAS.*,
       CA06_CONTADOR.CONTADOR
  FROM EMPRESAS
 INNER JOIN SC00_EMPRESA
    ON SC00_EMPRESA.EMPRESA = COD_EMPRESA
  LEFT JOIN SC00_EMPRESACERTDIG
    ON SC00_EMPRESACERTDIG.IDEMPRESA = SC00_EMPRESA.IDEMPRESA
  LEFT JOIN CA06_CONTADOR
    ON CA06_CONTADOR.IDCONTADOR = SC00_EMPRESACERTDIG.IDCONTADOR
 ORDER BY 1