SELECT EM.EMPRESA,
       ES.TPEVENTO,
       ES.DESCRICAOEVENTO,
       ES.CODIGOESOCIAL,
	   CAST(RE.DESCRICAO AS VARCHAR(100)) as NUMERO_RECIBO,
	   LEFT(ES.INIVALID, 10) AS DATA
  FROM SC00_ESOCIALREINFXML AS ES
 INNER JOIN SC00_EMPRESA AS EM
    ON ES.IDEMPRESA = EM.IDEMPRESA
 INNER JOIN SC00_ESOCIALREINFDET AS RE
    ON ES.IDESOCIALREINFXML = RE.IDEVENTO
 WHERE ES.TPEVENTO IN ('S-1005', 'S-1000', 'S-1020', 'S-1030', 'S-2200', 'S-2205', 'S-2206', 'S-2230', 'S-2300')
   AND LEFT(DESCRICAO, 3) = '1.1'
 ORDER BY 1, 2, 5;