SELECT SC00_EMPRESA.EMPRESA,
       CASE SC00_EMPRESACFG.INFORMACAO1
         WHEN '01/01/2018' THEN 1
         WHEN '08/01/2018' THEN 1
         WHEN '01/07/2018' THEN 2
         WHEN '16/07/2018' THEN 2
         WHEN '01/01/2019' THEN 3
         WHEN '10/01/2019' THEN 3
       END AS GRUPO_ESOCIAL
  FROM SC00_EMPRESACFG
 INNER JOIN SC00_EMPRESA
    ON SC00_EMPRESA.IDEMPRESA = SC00_EMPRESACFG.IDEMPRESA
 WHERE SC00_EMPRESACFG.ORIGEM = 90
   AND SC00_EMPRESACFG.ESPECIE = 38
   AND SC00_EMPRESACFG.TP = 10
ORDER BY 1;