SELECT FILIAIS.COD_EMPRESA,
       FILIAIS.COD_FILIAL,
       SC00_FILIALCFG.DATAINICIO,
       REPLACE(SC00_FILIALCFG.INFORMACAO1, ',', '.') AS FAP
  FROM SC00_FILIALCFG
 INNER JOIN FILIAIS
    ON FILIAIS.IDFILIAL = SC00_FILIALCFG.IDFILIAL
 WHERE SC00_FILIALCFG.ORIGEM = 90
   AND SC00_FILIALCFG.ESPECIE = 51
   AND SC00_FILIALCFG.TP = 1
   AND SC00_FILIALCFG.DATAINICIO IS NOT NULL
 ORDER BY FILIAIS.COD_EMPRESA,
          FILIAIS.COD_FILIAL;