SELECT MOV_TRANSF.COD_EMPRESA,
       MOV_TRANSF.COD_FILIAL,
       MOV_TRANSF.COD_EMPREGADO,
       MOV_TRANSF.COD_DEPTO,
       MOV_TRANSF.DATAINICIOMOV,
       MOV_TRANSF.DATAFIMMOV,
       MOV_TRANSF.MOTIVOINICIAL,
       MOV_TRANSF.MOTIVOFINAL,
       MOV_TRANSF.COD_SETOR,
       MOV_TRANSF.IDEMPREGADO,
       MOV_TRANSF.IDMOVTRANSF,
       MOV_TRANSF.IDFILIAL,
       EMPREGADOS.CONTRATO
  FROM MOV_TRANSF
  LEFT OUTER JOIN EMPREGADOS
    ON MOV_TRANSF.COD_EMPRESA = EMPREGADOS.COD_EMPRESA
   AND MOV_TRANSF.COD_EMPREGADO = EMPREGADOS.COD_EMPREGADO
 ORDER BY 1, 2, 3, 5;