SELECT EMPREGADOS.COD_EMPRESA,
       FP_PSAUDEOPERADORA.OPERADORA,
       EMPREGADOS.COD_EMPREGADO,
       FP_DEPENDENTE.DEPENDENTE,
       FP_PSAUDEVLRMESDEP.DATAINICIO,
       FP_PSAUDEVLRMESDEP.DATAFIM,
       FP_PSAUDEVLRMESDEP.VALOR
  FROM FP_PSAUDEVLRMESDEP
  LEFT JOIN FP_PSAUDEOPERADORA
    ON FP_PSAUDEOPERADORA.IDPSAUDEOPERADORA = FP_PSAUDEVLRMESDEP.IDPSAUDEOPERADORA
  LEFT JOIN EMPREGADOS
    ON EMPREGADOS.IDEMPREGADO = FP_PSAUDEVLRMESDEP.IDEMPREGADO
  LEFT JOIN FP_DEPENDENTE
    ON FP_DEPENDENTE.IDDEPENDENTE = FP_PSAUDEVLRMESDEP.IDDEPENDENTE
 ORDER BY 1, 2, 3;