SELECT EMPREGADOS.COD_EMPRESA,
       EMPREGADOS.COD_EMPREGADO,
       FP_EMPREGADOHORARIO.DATAINICIO,
       TAB_HORARIOS.CODIGO
  FROM FP_EMPREGADOHORARIO
  LEFT JOIN EMPREGADOS
    ON EMPREGADOS.IDEMPREGADO = FP_EMPREGADOHORARIO.IDEMPREGADO
  LEFT JOIN TAB_HORARIOS
    ON TAB_HORARIOS.IDHORARIO = FP_EMPREGADOHORARIO.IDHORARIO
 ORDER BY 1, 2, 3;
