SELECT COD_EMPRESA,
	   COD_EMPREGADO,
	   DATA_INICIO,
	   COD_VERBA,
	   REFERENCIA,
	   VALOR,
	   HORAS,
	   REF_CENTESIMAL,
	   VALORORIGINAL
FROM CALCULO INNER JOIN TAB_VERBAS
                     ON TAB_VERBAS.CODIGO = CALCULO.COD_VERBA
WHERE EXTRACT(YEAR FROM DATA_INICIO) >= (EXTRACT(YEAR FROM CURRENT_TIMESTAMP) - 3)
  AND VALORORIGINAL IS NULL
ORDER BY 1,2,3,4