SELECT OCORFUNC.OFCODEMP,
       OCORFUNC.OFMATFUNC,
       OCORFUNC.OFDTINIOCO,
       CASE
         WHEN OCORFUNC.OFCODOCORR <> 1016 THEN OCORFUNC.OFDTFINOCO
         ELSE NULL
       END AS OFDTFINOCO,
       OCORFUNC.OFDTAUX,
       CASE
         WHEN OCORFUNC.OFCODOCORR = 1016 THEN 99
         ELSE OCORFUNC.OFCODRAIS
       END AS OFCODRAIS,
       OCORFUNC.OFCODPROXS,
       OCORFUNC.OFCODNVANT
  FROM OCORFUNC
 WHERE OCORFUNC.OFCODOCORR IN (1002, 1016)
 ORDER BY 1, 2, 3