SELECT EMPRESAS.EMCODEMP,
       EMPRESAS.EMNOMEMP,
       EMPRESAS.EMNOMFANT,
       MUNICIP.MUDESMUNIC,
       MUNICIP.MUUF,
       LOTACOES.*
  FROM dbo.EMPRESAS
  LEFT JOIN dbo.LOTACOES
    ON LOTACOES.LOCODEMP = EMPRESAS.EMCODEMP
   AND LOTACOES.LOCODLOT = '1000000'
  LEFT JOIN dbo.MUNICIP
    ON MUNICIP.MUCODMUNIC = LOTACOES.LOCODMUNIC