SELECT CODIGOEMPRESA,
       '' AS CODIGO,
       '1000' AS TIPO_EVENTO,
       NUMERORECIBO AS RECIBO,
       '' AS CODIGO_ESOCIAL
  FROM EMPREGADORESPROCESSADOS
 WHERE NUMERORECIBO != ''
   AND NUMERORECIBO IS NOT NULL
 UNION
SELECT CODIGOEMPRESA,
       CAST(MATRICULA AS VARCHAR(255)) AS CODIGO,
       '2200' AS TIPO_EVENTO,
       NUMERORECIBO AS RECIBO,
       CAST(MATRICULA AS VARCHAR(255)) AS CODIGO_ESOCIAL
  FROM ADMISSOESPROCESSADAS
 WHERE NUMERORECIBO != ''
   AND NUMERORECIBO IS NOT NULL
 ORDER BY 1, 2, 3
