SELECT DISTINCT
       OFCODEMP AS CODI_EMP,
       OFMATFUNC AS I_EMPREGADOS,
       OFDTAUX,
       FORMAT(CONVERT(DATE, CAST(CAST(OFDTAUX AS INT) AS VARCHAR(8)), 112), 'dd/MM/yyyy') AS INI_AQUISITIVO,
	   OFCODOCORR,
       FORMAT(CONVERT(DATE, CAST(CAST(OFDTINIOCO AS INT) AS VARCHAR(8)), 112), 'dd/MM/yyyy') AS INI_GOZO
  FROM dbo.OCORFUNC
 WHERE OFCODOCORR IN (1001, 1007)
 ORDER BY 1, 2, 3