SELECT OFCODEMP AS CODI_EMP,
       OFMATFUNC AS I_EMPREGADOS,
       OFDTAUX,
       OFDTINIOCO,
       OFNUMPARC,
       FORMAT(CONVERT(DATE, CAST(CAST(OFDTAUX AS INT) AS VARCHAR(8)), 112), 'dd/MM/yyyy') AS INI_AQUISITIVO,
       FORMAT(DATEADD(DAY, -1, DATEADD(YEAR, 1, CONVERT(DATE, CAST(CAST(OFDTAUX AS INT) AS VARCHAR(8)), 112))), 'dd/MM/yyyy') AS FIM_AQUISITIVO,
       FORMAT(CONVERT(DATE, CAST(CAST(OFDTINIOCO AS INT) AS VARCHAR(8)), 112), 'dd/MM/yyyy') AS INI_GOZO,
       FORMAT(CONVERT(DATE, CAST(CAST(OFDTFINOCO AS INT) AS VARCHAR(8)), 112), 'dd/MM/yyyy') AS FIM_GOZO,
       OFNUMDIAFE AS DIAS_FERIAS,
       OFINDABONO AS ABONO,
       OFNUMDIAAB AS DIAS_ABONO,
	   OFCODOCORR
  FROM dbo.OCORFUNC
 WHERE OFCODOCORR IN (1001, 1007)
 ORDER BY 1, 2, 3, 4