SELECT DISTINCT
       FUNCIONA.FUCODEMP,
       FUNCIONA.FUCODHORAR,
       HORARIOS.HODESHORAR,
       HORARIOSCOMPLEMENT.CODIGOTIPOJORNADA
  FROM dbo.FUNCIONA
  LEFT JOIN dbo.HORARIOS
    ON HORARIOS.HOCODIGO = FUNCIONA.FUCODHORAR
  LEFT JOIN dbo.HORARIOSCOMPLEMENT
    ON HORARIOSCOMPLEMENT.CODIGOEMPRESA = HORARIOS.HOCODEMP
   AND HORARIOSCOMPLEMENT.CODIGOHORARIO = FUNCIONA.FUCODHORAR
 ORDER BY 1, 2