SELECT DISTINCT
       FUNCIONA.FUCODEMP,
       HORARIOSCONTRAT.CODIGOHORARIO,
       HORARIOSCONTRAT.CODIGODIASEMANA,
       HORARIOSCOMPLEMENT.CODIGOTIPOJORNADA,
       HORARIOSCONTRAT.HORAENTRADA,
       HORARIOSCONTRAT.HORASAIDA,
       HORARIOSINTERVALO.HORAINCIO AS INICIO_INTERVALO,
       HORARIOSINTERVALO.HORAFIM AS FIM_INTERVALO
  FROM dbo.FUNCIONA
  LEFT JOIN dbo.HORARIOSCONTRAT
    ON HORARIOSCONTRAT.CODIGOHORARIO = FUNCIONA.FUCODHORAR
  LEFT JOIN dbo.HORARIOSINTERVALO
    ON HORARIOSINTERVALO.CODIGOEMPRESA = HORARIOSCONTRAT.CODIGOEMPRESA
   AND HORARIOSINTERVALO.CODIGOHORARIO = HORARIOSCONTRAT.CODIGOHORARIO
   AND HORARIOSINTERVALO.CODIGODIASEMANA = HORARIOSCONTRAT.CODIGODIASEMANA
  LEFT JOIN dbo.HORARIOSCOMPLEMENT
    ON HORARIOSCOMPLEMENT.CODIGOEMPRESA = HORARIOSCONTRAT.CODIGOEMPRESA
   AND HORARIOSCOMPLEMENT.CODIGOHORARIO = FUNCIONA.FUCODHORAR
 WHERE FUNCIONA.FUCODEMP IS NOT NULL
   AND HORARIOSCONTRAT.CODIGOHORARIO IS NOT NULL
   AND HORARIOSCONTRAT.CODIGODIASEMANA IS NOT NULL
 ORDER BY 1, 2