SELECT OCORFUNC.OFCODEMP,
       OCORFUNC.OFMATFUNC,
       OCORFUNC.OFDTINIOCO,
       OCORFUNC.OFCODLTANT
  FROM dbo.OCORFUNC
  LEFT JOIN dbo.LOTACOES
    ON LOTACOES.LOCODEMP = OCORFUNC.OFCODEMP
   AND LOTACOES.LOCODLOT = OCORFUNC.OFCODLTANT
 WHERE OCORFUNC.OFCODOCORR = 1003
   AND LOTACOES.LOTIPOLOT NOT IN ('F', 'M')
   AND LOTACOES.LOIDCGCCEI = 'E'
 ORDER BY OFCODEMP ASC,
          OFMATFUNC ASC,
          OFDTINIOCO ASC