SELECT 
    CD_EMPRESA,
    CD_HORARIO,
    LPAD(REPLACE(CAST(HORA_ENTRADA AS VARCHAR(5)), '.', ':'), 5, '0') || ':00' AS HORA_ENTRADA_FORMATADA,
    LPAD(REPLACE(CAST(HORA_INTER1 AS VARCHAR(5)), '.', ':'), 5, '0') || ':00' AS HORA_INTER1_FORMATADA,
    LPAD(REPLACE(CAST(HORA_INTER2 AS VARCHAR(5)), '.', ':'), 5, '0') || ':00' AS HORA_INTER2_FORMATADA,
    LPAD(REPLACE(CAST(HORA_SAIDA AS VARCHAR(5)), '.', ':'), 5, '0') || ':00' AS HORA_SAIDA_FORMATADA
FROM 
    RHCADHORARIO
