SELECT
    DAI.IDOWNER,
    DAI.IDDADOSAGENTEINTEGRACAO,
    DAI.IDPESSOA,
    DAI.IDOWNER,
    BP.NOMEPESSOA AS NOME,
    BP.DATACADASTRAMENTO AS DATACADASTRAMENTO,
    BPE.LOGRADOURO AS ENDERECO,
    BPE.NUMEROENDERECO AS NUMERO,
    BPE.COMPLEMENTO AS COMPLEMENTO,
    BPE.BAIRRO AS BAIRRO,
    BPE.CEP AS CEP,
    BPE.IDMUNICIPIO AS MUNICIPIO,
    BDJ.CNPJ AS CNPJ
FROM NG.DBO.BPM_DADOSAGENTEINTEGRACAO DAI
LEFT JOIN NG.DBO.BPM_PESSOA BP ON DAI.IDPESSOA = BP.IDPESSOA
LEFT JOIN NG.DBO.BPM_PESSOAENDERECO BPE ON DAI.IDPESSOA = BPE.IDPESSOA
LEFT JOIN NG.DBO.BPM_DADOSPESSOAJURIDICA BDJ ON DAI.IDPESSOA = BDJ.IDPESSOA;
