SELECT
BPM_DADOSBANCO.IDDADOSBANCO,
BPM_DADOSAGENCIA.IDDADOSAGENCIA,
(SELECT BPM_PESSOA.NOMEPESSOA FROM NG.DBO.BPM_PESSOA WHERE BPM_DADOSBANCO.IDPESSOA = BPM_PESSOA.IDPESSOA) AS BANCO,
BPM_DADOSBANCO.CODIGOBANCO,
(SELECT BPM_PESSOA.NOMEPESSOA FROM NG.DBO.BPM_PESSOA WHERE BPM_DADOSAGENCIA.IDPESSOA = BPM_PESSOA.IDPESSOA) AS AGENCIA,
BPM_DADOSAGENCIA.DESCRICAO,
BPM_DADOSAGENCIA.CODIGOAGENCIA,
BPM_DADOSAGENCIA.NUMDVAGENCIA
FROM NG.DBO.BPM_DADOSAGENCIA, NG.DBO.BPM_DADOSBANCO
WHERE BPM_DADOSAGENCIA.IDDADOSBANCO = BPM_DADOSBANCO.IDDADOSBANCO
ORDER BY 1, 2