SELECT
(SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOCENTROCUSTO.IDREGISTRO) AS IDOWNER,
(SELECT FLH_REGISTRO.CODIGOREGISTRO FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOCENTROCUSTO.IDREGISTRO) AS IDREGISTRO,
FLH_MOVIMENTOCENTROCUSTO.DATAINICIO,
FLH_MOVIMENTOCENTROCUSTO.IDCENTROCUSTO,
IDPESSOAEMPRESA,
FLH_MOVIMENTOFILIAL.DATAFIM,
DESCRICAOCENTROCUSTO
FROM NG_FOLHA.DBO.FLH_MOVIMENTOCENTROCUSTO

LEFT JOIN NG.DBO.BPM_CENTROCUSTO
ON (SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOCENTROCUSTO.IDREGISTRO) = 
BPM_CENTROCUSTO.IDOWNER AND
FLH_MOVIMENTOCENTROCUSTO.IDCENTROCUSTO = 
BPM_CENTROCUSTO.idcentrocusto

LEFT JOIN NG_FOLHA.DBO.FLH_MOVIMENTOFILIAL 
ON (SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOCENTROCUSTO.IDREGISTRO) = 
(SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOFILIAL.IDREGISTRO) AND
(SELECT FLH_REGISTRO.CODIGOREGISTRO FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOCENTROCUSTO.IDREGISTRO) = 
(SELECT FLH_REGISTRO.CODIGOREGISTRO FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOFILIAL.IDREGISTRO)

ORDER BY 1,2,3