SELECT
NG.DBO.BPM_DADOSSUPERVISORESTAGIO.IDOWNER,
NG.DBO.BPM_DADOSSUPERVISORESTAGIO.IDDADOSSUPERVISORESTAGIO,
NG.DBO.BPM_DADOSSUPERVISORESTAGIO.IDPESSOA,
NG.DBO.BPM_DADOSSUPERVISORESTAGIO.IDOWNER,
(SELECT NG.DBO.BPM_PESSOA.NOMEPESSOA FROM NG.DBO.BPM_PESSOA WHERE NG.DBO.BPM_PESSOA.IDPESSOA = NG.DBO.BPM_DADOSSUPERVISORESTAGIO.IDPESSOA) AS NOME,
(SELECT NG.DBO.BPM_PESSOA.DATACADASTRAMENTO FROM NG.DBO.BPM_PESSOA WHERE NG.DBO.BPM_PESSOA.IDPESSOA = NG.DBO.BPM_DADOSSUPERVISORESTAGIO.IDPESSOA) AS DATACADASTRAMENTO,
(SELECT NG.DBO.BPM_DADOSPESSOAFISICA.CPF FROM NG.DBO.BPM_DADOSPESSOAFISICA WHERE NG.DBO.BPM_DADOSPESSOAFISICA.IDPESSOA = NG.DBO.BPM_DADOSSUPERVISORESTAGIO.IDPESSOA) AS CPF,
(SELECT NG.DBO.BPM_DADOSPESSOAFISICA.IDGRAUINSTRUCAO FROM NG.DBO.BPM_DADOSPESSOAFISICA WHERE NG.DBO.BPM_DADOSPESSOAFISICA.IDPESSOA = NG.DBO.BPM_DADOSSUPERVISORESTAGIO.IDPESSOA) AS GRAU_INSTRUCAO
FROM NG.DBO.BPM_DADOSSUPERVISORESTAGIO