SELECT
(SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTODEPARTAMENTO.IDREGISTRO) AS IDOWNER,
(SELECT FLH_REGISTRO.CODIGOREGISTRO FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTODEPARTAMENTO.IDREGISTRO) AS IDREGISTRO,
FLH_MOVIMENTODEPARTAMENTO.DATAINICIO,
FLH_MOVIMENTODEPARTAMENTO.IDDEPARTAMENTO,
IDPESSOAEMPRESA,
FLH_MOVIMENTOFILIAL.DATAFIM,
RH_DEPARTAMENTO.IDDEPARTAMENTO AS IDDEPARTAMENTO2,
CODIGODEPARTAMENTO,
DESCRICAODEPARTAMENTO
FROM NG_FOLHA.DBO.FLH_MOVIMENTODEPARTAMENTO

LEFT JOIN NG_RH.DBO.RH_DEPARTAMENTO
ON FLH_MOVIMENTODEPARTAMENTO.IDDEPARTAMENTO = RH_DEPARTAMENTO.IDDEPARTAMENTO

LEFT JOIN NG_FOLHA.DBO.FLH_MOVIMENTOFILIAL 
ON (SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTODEPARTAMENTO.IDREGISTRO) = 
(SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOFILIAL.IDREGISTRO) AND
(SELECT FLH_REGISTRO.CODIGOREGISTRO FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTODEPARTAMENTO.IDREGISTRO) = 
(SELECT FLH_REGISTRO.CODIGOREGISTRO FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOFILIAL.IDREGISTRO)

ORDER BY 1,2,3