SELECT 
FLH_REGISTRO.IDOWNER,
FLH_REGISTRO.CODIGOREGISTRO,
FLH_MOVIMENTOFILIAL.IDPESSOAEMPRESA,
FLH_MOVIMENTOFILIAL.DATAFIM,
(SELECT BPM_PESSOA.NOMEPESSOA FROM NG.DBO.BPM_PESSOA WHERE BPM_PESSOA.IDPESSOA = BPM_PESSOARELACIONAMENTO.IDPESSOAPRINCIPAL) AS PRINCIPAL,
(SELECT BPM_DADOSPESSOAFISICA.INDSEXO FROM NG.DBO.BPM_DADOSPESSOAFISICA WHERE BPM_DADOSPESSOAFISICA.IDPESSOA = BPM_PESSOARELACIONAMENTO.IDPESSOAPRINCIPAL) AS SEXO

FROM NG.DBO.BPM_PESSOARELACIONAMENTO,
     NG_FOLHA.DBO.FLH_REGISTRO

LEFT JOIN NG_FOLHA.DBO.FLH_MOVIMENTOFILIAL 
ON FLH_REGISTRO.IDOWNER = 
(SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOFILIAL.IDREGISTRO) AND
FLH_REGISTRO.CODIGOREGISTRO = 
(SELECT FLH_REGISTRO.CODIGOREGISTRO FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOFILIAL.IDREGISTRO)

WHERE FLH_REGISTRO.IDPESSOAREGISTRO = BPM_PESSOARELACIONAMENTO.IDPESSOASECUNDARIA
  AND BPM_PESSOARELACIONAMENTO.IDTIPORELACIONAMENTOPESSOA IN (2,3)

ORDER BY 1,2