SELECT
NG.DBO.BPM_DADOSINSTITUICAOENSINO.IDOWNER,
NG.DBO.BPM_DADOSINSTITUICAOENSINO.IDDADOSINSTITUICAOENSINO,
NG.DBO.BPM_DADOSINSTITUICAOENSINO.IDPESSOA,
NG.DBO.BPM_DADOSINSTITUICAOENSINO.IDOWNER,
(SELECT NG.DBO.BPM_PESSOA.NOMEPESSOA FROM NG.DBO.BPM_PESSOA WHERE NG.DBO.BPM_PESSOA.IDPESSOA = NG.DBO.BPM_DADOSINSTITUICAOENSINO.IDPESSOA) AS NOME,
(SELECT NG.DBO.BPM_PESSOA.DATACADASTRAMENTO FROM NG.DBO.BPM_PESSOA WHERE NG.DBO.BPM_PESSOA.IDPESSOA = NG.DBO.BPM_DADOSINSTITUICAOENSINO.IDPESSOA) AS DATACADASTRAMENTO,
BPM_PESSOAENDERECO.LOGRADOURO AS ENDERECO,
BPM_PESSOAENDERECO.NUMEROENDERECO AS NUMERO,
BPM_PESSOAENDERECO.COMPLEMENTO AS COMPLEMENTO,
BPM_PESSOAENDERECO.BAIRRO AS BAIRRO,
BPM_PESSOAENDERECO.CEP AS CEP,
BPM_PESSOAENDERECO.IDMUNICIPIO AS MUNICIPIO,
(SELECT NG.DBO.BPM_DADOSPESSOAJURIDICA.CNPJ FROM NG.DBO.BPM_DADOSPESSOAJURIDICA WHERE NG.DBO.BPM_DADOSPESSOAJURIDICA.IDPESSOA = NG.DBO.BPM_DADOSINSTITUICAOENSINO.IDPESSOA) AS CNPJ
FROM NG.DBO.BPM_DADOSINSTITUICAOENSINO
LEFT JOIN NG.DBO.BPM_PESSOAENDERECO ON (NG.DBO.BPM_PESSOAENDERECO.IDPESSOA = NG.DBO.BPM_DADOSINSTITUICAOENSINO.IDPESSOA
AND NG.DBO.BPM_PESSOAENDERECO.IDENDERECO = (SELECT MIN(T1.IDENDERECO) FROM NG.DBO.BPM_PESSOAENDERECO AS T1 WHERE T1.IDPESSOA = NG.DBO.BPM_PESSOAENDERECO.IDPESSOA))