SELECT
BPM_DADOSSOCIO.IDOWNER,
BPM_DADOSSOCIO.IDDADOSSOCIO,
(SELECT BPM_PESSOA.NOMEPESSOA FROM NG.DBO.BPM_PESSOA WHERE BPM_PESSOA.IDPESSOA = BPM_PESSOARELACIONAMENTO.IDPESSOAPRINCIPAL) AS PRINCIPAL,
(SELECT BPM_DADOSPESSOAFISICA.INDSEXO FROM NG.DBO.BPM_DADOSPESSOAFISICA WHERE BPM_DADOSPESSOAFISICA.IDPESSOA = BPM_PESSOARELACIONAMENTO.IDPESSOAPRINCIPAL) AS SEXO

 FROM NG.DBO.BPM_DADOSSOCIO,
      NG.DBO.BPM_PESSOARELACIONAMENTO

WHERE BPM_PESSOARELACIONAMENTO.IDTIPORELACIONAMENTOPESSOA IN (2,3)
AND BPM_DADOSSOCIO.IDPESSOA = IDPESSOASECUNDARIA

ORDER BY 1, 2