SELECT
(SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOSALARIO.IDREGISTRO) AS IDOWNER,
(SELECT FLH_REGISTRO.CODIGOREGISTRO FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOSALARIO.IDREGISTRO) AS IDREGISTRO,
FLH_MOVIMENTOSALARIO.DATAINICIO,
SALARIO,
IDPESSOAEMPRESA,
FLH_MOVIMENTOFILIAL.DATAFIM,
MOTIVOMOVIMENTO
FROM NG_FOLHA.DBO.FLH_MOVIMENTOSALARIO

LEFT JOIN NG_FOLHA.DBO.FLH_MOVIMENTOFILIAL 
ON (SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOSALARIO.IDREGISTRO) = 
(SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOFILIAL.IDREGISTRO) AND
(SELECT FLH_REGISTRO.CODIGOREGISTRO FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOSALARIO.IDREGISTRO) = 
(SELECT FLH_REGISTRO.CODIGOREGISTRO FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOFILIAL.IDREGISTRO)

ORDER BY 1,2,3