SELECT
(SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOSINDICATO.IDREGISTRO) AS IDOWNER,
(SELECT FLH_REGISTRO.CODIGOREGISTRO FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOSINDICATO.IDREGISTRO) AS IDREGISTRO,
FLH_MOVIMENTOSINDICATO.DATAINICIO,
IDDADOSSINDICATO,
IDPESSOAEMPRESA,
FLH_MOVIMENTOFILIAL.DATAFIM
FROM NG_FOLHA.DBO.FLH_MOVIMENTOSINDICATO

LEFT JOIN NG_FOLHA.DBO.FLH_MOVIMENTOFILIAL 
ON (SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOSINDICATO.IDREGISTRO) = 
(SELECT FLH_REGISTRO.IDOWNER FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOFILIAL.IDREGISTRO) AND
(SELECT FLH_REGISTRO.CODIGOREGISTRO FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOSINDICATO.IDREGISTRO) = 
(SELECT FLH_REGISTRO.CODIGOREGISTRO FROM NG_FOLHA.DBO.FLH_REGISTRO WHERE FLH_REGISTRO.IDREGISTRO = FLH_MOVIMENTOFILIAL.IDREGISTRO)

ORDER BY 1,2,3