SELECT RHCONTRATOS.UNIDADE,
	   RHCARGOS.CARGO,
	   RHCARGOS.DESCRICAOOFICIAL,
	   RHCARGOS.CBO,
	   RHCARGOS.CBONOVO
FROM RHCONTRATOS
INNER JOIN RHCARGOS
ON RHCONTRATOS.CARGO = RHCARGOS.CARGO

UNION

SELECT RHCONTRATOS.UNIDADE,
	   RHCARGOS.CARGO,
	   RHCARGOS.DESCRICAOOFICIAL,
	   RHCARGOS.CBO,
	   RHCARGOS.CBONOVO
FROM RHALTERACOESCONTRATO
INNER JOIN RHCONTRATOS
ON RHCONTRATOS.UNIDADE = RHALTERACOESCONTRATO.UNIDADE
AND RHCONTRATOS.CONTRATO = RHALTERACOESCONTRATO.CONTRATO
INNER JOIN RHCARGOS
ON RHALTERACOESCONTRATO.CODIGOANTERIOR = RHCARGOS.CARGO
WHERE VARIAVEL = 'CCAR' and CODIGOATUAL != ''

UNION

SELECT RHCONTRATOS.UNIDADE,
	   RHCARGOS.CARGO,
	   RHCARGOS.DESCRICAOOFICIAL,
	   RHCARGOS.CBO,
	   RHCARGOS.CBONOVO
FROM RHALTERACOESCONTRATO
INNER JOIN RHCONTRATOS
ON RHCONTRATOS.UNIDADE = RHALTERACOESCONTRATO.UNIDADE
AND RHCONTRATOS.CONTRATO = RHALTERACOESCONTRATO.CONTRATO
INNER JOIN RHCARGOS
ON RHALTERACOESCONTRATO.CODIGOATUAL = RHCARGOS.CARGO
WHERE VARIAVEL = 'CCAR' and CODIGOATUAL != ''

ORDER BY 1, 2