SELECT RHCONTRATOS.UNIDADE,
	   RHCONTRATOS.CONTRATO,
	   RHPESSOAS.CONSELHOCLASSE,
	   RHPESSOAS.REGISTROCONSELHO,
	   RHPESSOAS.DATAREGISTRO
FROM RHCONTRATOS
INNER JOIN RHPESSOAS
ON RHCONTRATOS.EMPRESA = RHPESSOAS.EMPRESA
AND RHCONTRATOS.PESSOA = RHPESSOAS.PESSOA
WHERE CONSELHOCLASSE IS NOT NULL
ORDER BY RHCONTRATOS.UNIDADE, RHCONTRATOS.CONTRATO