    SELECT distinct RHESOCIALEVENTOS2230.EMPRESA,
		   dbo.RHCONTRATOS.CONTRATO AS CODIGO,
           RHESOCIALEVENTOS2230.NRRECIBOESOCIAL,
		   RHESOCIALEVENTOS2230.MATRICULAESOCIAL AS CODIGO_ESOCIAL,
		   RHESOCIALEVENTOS2230.TIPOEVENTOESOCIAL,
		   RHESOCIALEVENTOS2230.DATATERMINOOCORRENCIA AS AFASTAMENTO
      FROM RHESOCIALEVENTOS2230
LEFT JOIN dbo.RHPESSOAS ON dbo.RHESOCIALEVENTOS2230.CPF = dbo.RHPESSOAS.CPF
						      and RHESOCIALEVENTOS2230.EMPRESA = dbo.RHPESSOAS.EMPRESA
LEFT JOIN dbo.RHCONTRATOS ON  dbo.RHCONTRATOS.PESSOA = dbo.RHPESSOAS.PESSOA
                                   and dbo.RHCONTRATOS.EMPRESA = dbo.RHPESSOAS.EMPRESA
     WHERE EVENTOESOCIAL = (select max(t2.EVENTOESOCIAL)
							 from dbo.RHESOCIALEVENTOS2230 as t2
							where  t2.EMPRESA = RHESOCIALEVENTOS2230.EMPRESA
							  and t2.MATRICULAESOCIAL = RHESOCIALEVENTOS2230.MATRICULAESOCIAL
							  and t2.REGISTROEXCLUIDOESOCIAL = 0
							  AND SITUACAOESOCIAL = 3
							  AND NRRECIBOESOCIAL <> ('')
							  AND DATATERMINOOCORRENCIA <> (''))
  ORDER BY 1,4,6