SELECT RHFERIASCEDIDAS.*, RHPERAQUISITIVOS.DIASFALTADOSPERIODO
  FROM RHFERIASCEDIDAS
  LEFT JOIN RHPERAQUISITIVOS
         ON RHFERIASCEDIDAS.UNIDADE = RHPERAQUISITIVOS.UNIDADE
		AND RHFERIASCEDIDAS.CONTRATO = RHPERAQUISITIVOS.CONTRATO
		AND RHFERIASCEDIDAS.INICIOPERAQUISITIVO = RHPERAQUISITIVOS.INICIOPERAQUISITIVO
		AND RHFERIASCEDIDAS.TERMINOPERAQUISITIVO = RHPERAQUISITIVOS.TERMINOPERAQUISITIVO
  ORDER BY 1,2,3