SELECT RHCONTRATOS.UNIDADE,
	   RHESCALAS.ESCALA,
	   RHESCALAS.DESCRICAO60,
	   RHESCALAS.TIPOESCALA
FROM RHCONTRATOS
INNER JOIN RHESCALAS
ON RHCONTRATOS.ESCALA = RHESCALAS.ESCALA

UNION

SELECT RHCONTRATOS.UNIDADE,
	   RHESCALAS.ESCALA,
	   RHESCALAS.DESCRICAO60,
	   RHESCALAS.TIPOESCALA
FROM RHALTERACOESCONTRATO
INNER JOIN RHCONTRATOS
ON RHCONTRATOS.UNIDADE = RHALTERACOESCONTRATO.UNIDADE
AND RHCONTRATOS.CONTRATO = RHALTERACOESCONTRATO.CONTRATO
INNER JOIN RHESCALAS
ON RHALTERACOESCONTRATO.CODIGOANTERIOR = RHESCALAS.ESCALA
WHERE VARIAVEL = 'CESC' and CODIGOATUAL != ''

UNION

SELECT RHCONTRATOS.UNIDADE,
	   RHESCALAS.ESCALA,
	   RHESCALAS.DESCRICAO60,
	   RHESCALAS.TIPOESCALA
FROM RHALTERACOESCONTRATO
INNER JOIN RHCONTRATOS
ON RHCONTRATOS.UNIDADE = RHALTERACOESCONTRATO.UNIDADE
AND RHCONTRATOS.CONTRATO = RHALTERACOESCONTRATO.CONTRATO
INNER JOIN RHESCALAS
ON RHALTERACOESCONTRATO.CODIGOATUAL = RHESCALAS.ESCALA
WHERE VARIAVEL = 'CESC' and CODIGOATUAL != ''

ORDER BY 1, 2