SELECT CALCULOFOLHANORMAL.CODIGO,
       CALCULOFOLHANORMAL.MESBASE,
       CALCULOFOLHANORMAL.VALO,
       CALCULOFOLHANORMAL.CONT,
       EVENTOS."DESC",
       CALCULOFOLHANORMAL.PERC
  FROM CALCULOFOLHANORMAL
 INNER JOIN EVENTOS
    ON EVENTOS.CODI = CALCULOFOLHANORMAL.CONT
 WHERE CALCULOFOLHANORMAL.MESBASE SIMILAR TO '[0-9]+'
   AND EVENTOS.ACUM = 'S'
   AND CAST(LEFT(CALCULOFOLHANORMAL.MESBASE, 4) AS INTEGER) >= (EXTRACT(YEAR FROM CURRENT_DATE) -3)
 ORDER BY 2, 1;