SELECT DISTINCT horarioemp.codempresa,
       HORARIO.DOMENT1 AS ENTRADA1,
       HORARIO.DOMSAI1 AS SAIDA1,
       HORARIO.DOMENT2 AS ENTRADA2,
       HORARIO.DOMSAI2 AS SAIDA2
  FROM NFP000.horarioemp
  LEFT JOIN NFP000.HORARIO
    ON HORARIO.codhorario = horarioemp.codhorario
 WHERE HORARIO.DOMENT1 IS NOT NULL
UNION
