SELECT horarioemp.codempresa,
       horarioemp.codhorario,
       HORARIO.DESCRICAO,
       HORARIO.DOMENT1,
       HORARIO.DOMSAI1,
       HORARIO.DOMENT2,
       HORARIO.DOMSAI2,
       HORARIO.SEGENT1,
       HORARIO.SEGSAI1,
       HORARIO.SEGENT2,
       HORARIO.SEGSAI2,
       HORARIO.TERENT1,
       HORARIO.TERSAI1,
       HORARIO.TERENT2,
       HORARIO.TERSAI2,
       HORARIO.QUAENT1,
       HORARIO.QUASAI1,
       HORARIO.QUAENT2,
       HORARIO.QUASAI2,
       HORARIO.QUIENT1,
       HORARIO.QUISAI1,
       HORARIO.QUIENT2,
       HORARIO.QUISAI2,
       HORARIO.SEXENT1,
       HORARIO.SEXSAI1,
       HORARIO.SEXENT2,
       HORARIO.SEXSAI2,
       HORARIO.SABENT1,
       HORARIO.SABSAI1,
       HORARIO.SABENT2,
       HORARIO.SABSAI2
  FROM NFP000.horarioemp
  LEFT JOIN NFP000.HORARIO
    ON HORARIO.codhorario = horarioemp.codhorario
 ORDER BY 1, 2;