SELECT PHTEMP.CODEMP,
	   PHTEMP.RAZSOC,
	   PHTEMP.NOMINT,
	   PHTEMP.OBSERV,
	   PHTEMP.ATIINI,
	   PHTEMP.ATIFIN,
	   PHTEMP.INSEST,
	   PHTEMP.INSMUN,
	   PHTEMP.ATICOD,
	   PHTEMP2.RAINJR,
	   PHTEMP2.GPSFAP,
	   PHTEMP.NUMINS,
	   PHTEMP.NUMCEI,
	   PHTEMP.NUMCAE,
	   PHTEMP.CODMUN,
	   PHTEMP.TIPLOG,
	   PHTEMP.ENDERE,
	   PHTEMP.NUMERO,
	   PHTEMP.COMPLE,
	   PHTEMP.BAIRRO,
	   PHTEMP.CIDADE,
	   PHTEMP.ESTADO,
	   PHTEMP.NUMCEP,
	   PHTEMP.CMPCEP,
	   PHTEMP.E_MAIL,
	   PHTEMP.ENDNET,
	   PHTEMP.NUMDDD,
	   PHTEMP.TELEFO,
	   PHTEMP.RESNOM,
	   PHTEMP.RESDO1,
	   PHTEMP.RESDO2,
	   PHTEMP.RESFO1
FROM PHTEMP
LEFT JOIN PHTEMP2
	ON PHTEMP2.CODEMP = PHTEMP.CODEMP
	AND PHTEMP2.ANOMES = (SELECT FIRST 1 SUB.ANOMES FROM PHTEMP2 AS SUB WHERE SUB.CODEMP = PHTEMP.CODEMP ORDER BY SUB.ANOMES DESC)
ORDER BY 1