SELECT PHWEFDS.CODEMP,
       PHWEFDS.CODCAD,
       PHWEFDS.IDEARQ,
       PHWEFDS.CODIG2,
       PHWEFDS.CODIG1,
       PHWEFDS.RECEFD,
       PHWEFDS.VALINI
FROM PHWEFDS
WHERE PHWEFDS.IDEARQ IN ('S-1000', 'S-1030', 'S-2200', 'S-2205', 'S-2206', 'S-2300')
	AND PHWEFDS.RECEFD <> ''
	AND PHWEFDS.RECEFD LIKE '1.1.%'
ORDER BY 1, 3, 7