SELECT TABJOR AS CODEMP,
       CODJOR,
       DESCRI,
       HORENT,
       HORSAI,
       INIIN1,
       FININ1
FROM PHTJOR
WHERE TABJOR != '0000'

UNION ALL

SELECT PHTEMP.CODEMP AS CODEMP,
       CODJOR,
       DESCRI,
       HORENT,
       HORSAI,
       INIIN1,
       INIIN1
FROM PHTJOR,
     PHTEMP
WHERE TABJOR != '0000'
  AND LPAD(TABJOR,4,0) = LPAD(PHTEMP.CODMAT,4,0)
  AND NOT EXISTS(SELECT 1
                   FROM PHTJOR AS E2
                  WHERE LPAD(E2.TABJOR,4,0) = LPAD(PHTEMP.CODEMP,4,0))
GROUP BY 1, 2, 3, 4, 5, 6, 7
ORDER BY 1, 2